function Counter() {
  const [count, setCount] = useState(0);

  return (
    <div>
      <p>Count: {count}</p>
      <button onClick={() => setCount(count + 1)}>
        Increment
      </button>
    </div>
  );
}

function Greeting(props) {
  const isLoggedIn = props.isLoggedIn;

  if (isLoggedIn) {
    return <h1>Welcome back!</h1>;
  }
  return <h1>Please sign up.</h1>;
}

function NumberList(props) {
  const numbers = props.numbers;
  const listItems = numbers.map((number) =>
    <li key={number.toString()}>
      {number}
    </li>
  );

  return (
    <ul>{listItems}</ul>
  );
}

// React creates a virtual DOM tree
// Compares with previous tree (diffing)
// Calculates most efficient updates (reconciliation)
// Only updates what's necessary in real DOM

function CounterApp() {
  const [count, setCount] = React.useState(0);

  return (
    <div>
      <h1>Counter: {count}</h1>
      <button onClick={() => setCount(count + 1)}>+1</button>
      <button onClick={() => setCount(count - 1)}>-1</button>
      <button onClick={() => setCount(0)}>Reset</button>
    </div>
  );
}

function UserCard({ user, onEdit, onDelete }) {
  return (
    <div className="user-card">
      <h3>{user.name}</h3>
      <p>{user.email}</p>
      <button onClick={() => onEdit(user)}>Edit</button>
      <button onClick={() => onDelete(user.id)}>Delete</button>
    </div>
  );
}

function UserProfile({ userId }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUser(userId).then(userData => {
      setUser(userData);
      setLoading(false);
    });
  }, [userId]);

  if (loading) return <div>Loading...</div>;
  return <UserProfileView user={user} />;
}

function FormComponent() {
  const [formData, setFormData] = useState({ name: '', email: '' });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" value={formData.name} onChange={handleChange} />
      <input name="email" value={formData.email} onChange={handleChange} />
      <button type="submit">Submit</button>
    </form>
  );
}

function Notification({ message, type, isVisible }) {
  return (
    isVisible && (
      <div className={`notification notification--${type}`}>
        {message}
        {type === 'error' && <ErrorDetails />}
      </div>
    )
  );
}

// Props: Read-only data passed from parent to child
// State: Mutable data managed within a component

function Parent() {
  const [count, setCount] = useState(0);

  return (
    <div>
      <ChildComponent count={count} /> // Props
      <button onClick={() => setCount(count + 1)}>
        Update State
      </button>
    </div>
  );
}

function ProductCard({ product }) {
  return (
    <div className="product-card">
      <h2>{product.name}</h2>
      <p className="price ${product.onSale ? 'sale' : ''}">
        ${product.price}
        {product.onSale && (
          <span className="original-price">
            ${product.originalPrice}
          </span>
        )}
      </p>
      <button
        disabled={!product.inStock}
        className={`btn ${!product.inStock ? 'btn--disabled' : 'btn--primary'}`}
      >
        {product.inStock ? 'Add to Cart' : 'Out of Stock'}
      </button>
    </div>
  );
}

function createCounter(initialValue = 0) {
  let state = initialValue;
  const listeners = [];

  function setState(newState) {
    state = newState;
    listeners.forEach(listener => listener(state));
  }

  function subscribe(listener) {
    listeners.push(listener);
    return () => {
      const index = listeners.indexOf(listener);
      if (index > -1) listeners.splice(index, 1);
    };
  }

  return { getState: () => state, setState, subscribe };
}
